	&.vjs-error {
		.vjs-error-display {
			background-color: #000;

			&:before {
				content: none;
			}

			.vjs-errors-dialog {
				display: flex;
				justify-content: center;
				align-items: center;
				width: 100%;
				height: 100%;

				.bg {
					position: absolute;
					top: 0;
					left: 0;
					width: 100%;
					height: 100%;
					background-image: url("https://cdnstatic.cam.tv/images/placeholders/video-error-bg.gif");
