						width: 100%;
						object-fit: contain;
						animation: spin 7s linear infinite;
					}

					@keyframes spin {
						from {
							transform: rotate(0deg);
						}
						to {
							transform: rotate(360deg);
						}
					}
				}

				.error-message {
					z-index: 1;

					h3 {
						font-size: 1.6em;
						font-weight: 700;
						margin: 0;
						padding: 0.5em 0;
						text-shadow: 0 0.1em 0.3em rgba(0, 0, 0, 0.8);
					}

					p {
						font-size: 1.2em;
						margin: 0;
						padding: 0 0 1em;
						text-shadow: 0 0.1em 0.2em rgba(0, 0, 0, 0.8);
					}

					button {
						display: flex;
						align-items: center;
						justify-content: center;
						margin: auto;
					}
				}
			}
		}
	}
}
