$color-white-opacity-10: rgba(white, 0.1); // overflow (horizontal mini-topfan)
$color-white-opacity-50: rgba(white, 0.5); // white text inactive
$color-white-opacity-80: rgba(white, 0.8); // overflow (horizontal mini-topfan)

///////////////////////////////////////////////////////////////////////////////
// GENERAL COLORS
///////////////////////////////////////////////////////////////////////////////
$color-yellow: #ffca28;

