// NOTA: CREARE SCALA DI VALORI Z-INDEX

///////////////////////////////////////////////////////////////////////////////
// FONT
///////////////////////////////////////////////////////////////////////////////

// WEIGHT
$font-weight--light: 300;
$font-weight--regular: 400;
$font-weight--medium: 500;
$font-weight--bold: 700;

// RIPRENDERE REVISIONE DA QUI
$typo-font-size-extrasmall: 1rem;
$typo-font-size-small: 1.2rem;
$typo-font-size-little: 1.4rem;
$typo-font-size-normal: 1.5rem;
$typo-font-size-medium: 1.6rem; //Grandezza di base
$typo-font-size-large: 1.8rem;
$typo-font-size-big: 2rem;
$typo-font-size-verybig: 2.5rem;
$typo-font-size-extralarge: 3rem;

// BORDER-RADIUS
$border-radius--x-small: 0.3rem; // input
$border-radius--small: 0.5rem; // value most used
$border-radius--medium: 1rem;
$border-radius--rounded: 50%; // rounded elements

// MEDIA QUERY SIZES (on max-width)
$media-xs: 410px;
$media-s: 500px;
$media-m: 768px;
$media-l: 1024px;

///////////////////////////////////////////////////////////////////////////////
// MIXINS
///////////////////////////////////////////////////////////////////////////////
// Debug: called on body it returns a colored layout
@mixin debug($color: blue, $important: false) {
	@if $important {
		* {
			background: rgba($color, 0.05) !important;
		}
	} @else {
		* {
			background: rgba($color, 0.05);
		}
	}
}
/* TK: Matteo - CTBT-1328 REMOVED */
// GRID ------------------------------------- //
@mixin container($max-width: 100%, $padding: 3rem) {
	max-width: $max-width;
	padding-right: $padding;
	padding-left: $padding;
	margin-left: auto;
	margin-right: auto;
}

@mixin row($margin: 1.5rem) {
	display: flex;
	flex-wrap: wrap;
	margin-right: -$margin;
	margin-left: -$margin;
}

@mixin col-basic($padding: 1.5rem) {
	//min-height: 0.1rem;
	flex-shrink: 0;
	flex-grow: 0;
	padding-right: $padding;
	padding-left: $padding;
}

@mixin col-width($ratio: 1) {
	max-width: unquote($ratio * 100 + "%");
	flex-basis: unquote($ratio * 100 + "%");
}

@mixin col-offset($ratio: 0) {
	margin-left: unquote($ratio * 100 + "%");
}

@mixin col-no-gutter($margin: 1.5rem) {
	margin-right: -$margin;
	margin-left: -$margin;
}

// MEDIA QUERY ------------------------------ //
/* TK: Matteo - CTBT-1355 START */
@mixin tablet-large-and-beyond {
	@media screen and (max-width: $media-l) {
		@content;
	}
}

@mixin tablet-medium-and-beyond {
	@media screen and (max-width: $media-m) {
		@content;
	}
}

@mixin desktop-only {
	@media only screen and (min-width: $media-l + 1) {
		@content;
	}
}

@mixin tablet-only {
	@media only screen and (min-width: $media-s + 1) and (max-width: $media-l) {
		@content;
	}
}

@mixin tablet-large-only {
	@media only screen and (min-width: $media-m + 1) and (max-width: $media-l) {
		@content;
	}
}

@mixin tablet-medium-only {
	@media only screen and (min-width: $media-s + 1) and (max-width: $media-m) {
		@content;
	}
}

@mixin mobile-only {
	@media (max-width: $media-s) and (orientation: portrait) {
		@content;
	}

	@media (max-height: $media-s) and (orientation: landscape) {
		@content;
	}
}
/* TK: Matteo - CTBT-1355 END */

// LOADER PULSE ------------------------- //
@mixin mixin-loading-pulse-size($base-line-height) {
	width: ($base-line-height / 4);
	height: $base-line-height;

	&:before,
	&:after {
		width: ($base-line-height / 4);
		height: ($base-line-height / 1.5);
	}

	&:before {
		left: -($base-line-height / 2);
	}

	&:after {
		left: ($base-line-height / 2);
	}
}

@mixin mixin-loading-pulse-color($loader-color, $opacity, $pulse-duration, $animation) {
	opacity: $opacity;
	background-color: $loader-color;
	animation: #{$animation} $pulse-duration linear infinite alternate;
	animation-delay: ($pulse-duration / 3);

	&:before,
	&:after {
		opacity: $opacity;
		background: $loader-color;
		animation: #{$animation} $pulse-duration linear infinite alternate;
	}

	&:after {
		animation-delay: ($pulse-duration / 1.5);
	}

	@keyframes #{$animation} {
		50% {
			opacity: 1;
		}
	}
}

// TOOLTIPS ----------------------------- //
@mixin tooltip-width($width: 20rem) {
	&:after {
		width: $width;
		white-space: normal;
	}
}

// AVATAR ------------------------------- //
/* TK: STEFANO - CTBT-216 START */
@mixin make-avatar($collection) {
	@each $index in $collection {
		&.avatar-size-#{$index} {
			display: block;
			width: #{$index}rem;
			height: #{$index}rem;
			border-radius: $border-radius--rounded;
			flex-shrink: 0; // FIXES FIREFOX PROBLEM
			&.avatar-brd {
				@include avatar-brd;
			}

			&.avatar-brd-primary {
				@include avatar-brd(var(--color-primary--medium));
			}
		}
	}
}

@mixin avatar-rnd($size) {
	display: block;
	width: $size;
	height: $size;
	border-radius: $border-radius--rounded;
}

@mixin avatar-brd($brd-color: $color-gray-13, $padding: 0.2rem) {
	/*TK: Moreno - 20181116_09*/
	background-color: $color-white;
	padding: $padding; /*TK: Moreno - 20181116_09 START*/
	border: 0.1rem solid $brd-color;
}
/* TK: STEFANO - CTBT-216 END */

// FADE ON TEXT OVERFLOWING ------------- //
// TK: MORENO - CTBT-267
@mixin fade-text-overflow($text-height, $fade-height, $last-gradient: $color-white) {
	max-height: $text-height;
	overflow: hidden;
	position: relative;
	word-wrap: break-word; // TK: MORENO - CTBT-666
	//
	&:after {
		position: absolute;
		content: "";
		bottom: 0;
		right: 0;
		width: 3rem;
		height: $fade-height;
		background-image: linear-gradient(to right, $color-transparent, $last-gradient 80%);
	}
}

